    public List<BookDA> SelectAll() {
        List<BookDA> aListBookDA = new ArrayList<BookDA>();
        try {
            CallableStatement call = conn.prepareCall("{call spd_Book_SelectAll()}");
            call.execute();
            ResultSet rs = call.getResultSet();
            while (rs.next()) {
                BookDA aBookDA = new BookDA(rs.getInt(1), rs.getInt(2), rs.getString(3), rs.getBytes(4), rs.getInt(5), rs.getInt(6), rs.getFloat(7),  rs.getFloat(8), rs.getInt(9), rs.getInt(10));
                aListBookDA.add(aBookDA);
            }
        } catch (SQLException ex) {
            Logger.getLogger(BookBO.class.getName()).log(Level.SEVERE, null, ex);
        }
        return aListBookDA;
    }
    
