    public int SelectLast_IDBook(){
        try {
            PreparedStatement call = conn.prepareStatement("select IDENT_CURRENT('[LibrarianProject].[dbo].[Book]')");
            call.executeQuery();
            ResultSet rs = call.getResultSet();
            rs.next();
            return rs.getInt(1);
        } catch (SQLException ex) {
            Logger.getLogger(BookBO.class.getName()).log(Level.SEVERE, null, ex);
            return 0;
        }
    }

    public boolean Insert(BookDA aBookDA) {
        try {
            CallableStatement call = conn.prepareCall("{call spd_Book_Insert(?,?,?,?,?,?,?,?,?)}");
            call.setInt(1, aBookDA.getIDPublisher());
            call.setString(2, aBookDA.getName());
            call.setBytes(3, aBookDA.getImage());
            call.setInt(4, aBookDA.getPage());
            call.setInt(5, aBookDA.getPublishDate());
            call.setFloat(6, aBookDA.getCoverSizeWidth());
            call.setFloat(7, aBookDA.getCoverSizeHeight());
            call.setInt(8, aBookDA.getQuantity());
            call.setInt(9, aBookDA.getExistingNumber());
            if(call.executeUpdate() == 1){
                return true;
            }else{
                return false;
            }
            
        } catch (SQLException ex) {
            Logger.getLogger(BookBO.class.getName()).log(Level.SEVERE, null, ex);
            return false;
        }
    }
    
